function movieFunction(){$("#results").html("");var e=$("#searchform").val();$.ajax({type:"GET",dataType:"jsonp",url:"http://api.themoviedb.org/3/search/movie",data:"api_key=51a0abce642402e7b8d43b2081302e77&query="+e,success:function(e){console.log(e);for(var s="",t=0;t<e.results.length;t++)e.results[t].poster_path,s=null==e.results[t].poster_path?"img/nophoto.png":"http://image.tmdb.org/t/p/w154"+e.results[t].poster_path,$("#results").append('<div class="movie"><img class="poster" src="'+s+'"><div class="title">'+e.results[t].title+'</div><div class="rating">'+e.results[t].vote_average+'</div><div class="release">'+e.results[t].release_date+"</div></div>")}})}$(document).ready(function(){$("#submit").click(function(){movieFunction()}),$("#searchform").keypress(function(e){13==e.which&&movieFunction()})});