function movieFunction(){$("#results").html("");var t=$("#searchform").val();$.ajax({type:"GET",dataType:"jsonp",url:"http://api.themoviedb.org/3/search/movie",data:"api_key=51a0abce642402e7b8d43b2081302e77&query="+t,success:function(t){console.log(t);for(var e="",s=0;s<t.results.length;s++)t.results[s].poster_path,e=null==t.results[s].poster_path?"img/nophoto.png":"http://image.tmdb.org/t/p/w154"+t.results[s].poster_path,$("#results").append('<div class="movie"><img class="poster" src="'+e+'"><div class="title">'+t.results[s].title+"</div></div>")}})}$(document).ready(function(){$("#submit").click(function(){movieFunction()}),$("#searchform").keypress(function(t){13==t.which&&movieFunction()})});