function movieFunction(){$("#results").html("");var e=$("#searchform").val();$.ajax({type:"GET",dataType:"jsonp",url:"http://api.themoviedb.org/3/search/movie",data:"api_key=51a0abce642402e7b8d43b2081302e77&query="+e,success:function(e){console.log(e);for(var t="",s=0;s<e.results.length;s++)e.results[s].poster_path,t=null==e.results[s].poster_path?"img/placeholder.png":"http://image.tmdb.org/t/p/w154"+e.results[s].poster_path,$("#results").append('<div class="movie"><img class="poster" src="'+t+'"><div class="title">'+e.results[s].title+"</div></div>")}})}$(document).ready(function(){$("#submit").click(function(){movieFunction()}),$("#searchform").keypress(function(e){13==e.which&&movieFunction()})});